import React, { Component } from 'react';
import './App.css';
import axios from 'axios';
class App extends Component {
	state = {
		// Initially, no file is selected
		selectedFile: null,
		loading: true,
		files: [],
	};
	componentDidMount() {
		axios({
			method: 'get',
			url: 'http://localhost:4000/list',
		}).then((res) => {
			console.log(res.data);
			let filesUrl = [];
			for (let i = 0; i < res.data.files.length; i++) {
				let url = ''
				this.download(res.data.files[i],url);
				console.log('bloburl', url);
				filesUrl.push(url);
			}
			this.setState({ loading: false, files: filesUrl });
		});
	}
	// On file select (from the pop up)
	onFileChange = (event) => {
		// Update the state
		this.setState({ selectedFile: event.target.files[0] });
	};

	// On file upload (click the upload button)
	onFileUpload = () => {
		this.setState({ loading: true });
		let formData = new FormData();
		if (this.state.selectedFile && this.state.selectedFile.name) {
			formData.append('file', this.state.selectedFile);
			formData.append('name', this.state.selectedFile.name);
			axios({
				method: 'post',
				url: 'http://localhost:4000/upload',
				data: formData,
			}).then((res) => {
				console.log(res);
				this.setState({ loading: false, status: res.data.Message });
			});
			console.log(this.state.selectedFile);
		}
	};
	download = async (file, url) => {
		var element = document.createElement('a');
		let blob = await fetch(file).then((r) => r.blob());
		await console.log(blob);
		 url = await URL.createObjectURL(blob);

	};
	// File content to be displayed after
	// file upload is complete
	fileData = () => {
		console.log('nh', this.state.files);
		if (this.state.files.length > 0) {
			return (
				<div>
					{this.state.files.map((each) => (
						<a href={each} download>
							download
						</a>
					))}
					{/* <h2>File Details:</h2>

					<p>File Name: {this.state.selectedFile.name}</p>

					<p>File Type: {this.state.selectedFile.type}</p>

					<p>Last Modified: {this.state.selectedFile.lastModifiedDate.toDateString()}</p> */}
				</div>
			);
		} else {
			return (
				<div>
					<br />
					<h4>Choose before Pressing the Upload button</h4>
				</div>
			);
		}
	};

	render() {
		return (
			<div className="App">
				<header className="App-header">
					<label for="myfile">Select a file:</label>
					<input className="App-link" type="file" id="myfile" name="myfile" onChange={this.onFileChange} />
					<button onClick={this.onFileUpload}>Upload!</button>
					{this.fileData()}
					{this.state.loading ? 'Loading ...' : this.state.status}
				</header>
			</div>
		);
	}
}

export default App;
