import './App.css';

function App() {
  state = {

    // Initially, no file is selected
    selectedFile: null
  };

  // On file select (from the pop up)
  onFileChange = event => {

    // Update the state
    this.setState({ selectedFile: event.target.files[0] });

  };
	return (
		<div className="App">
			<header className="App-header">
				<label for="myfile">Select a file:</label>
				<input className="App-link" type="file" id="myfile" name="myfile" onChange={this.onFileChange}/>
			</header>
      <button onClick={this.onFileUpload}>
                  Upload!
                </button>
		</div>
	);
}

export default App;
